mapper.insert:
insert into board(
	subject, userid, content, type
) values (
	#{subject}, #{userid}, #{content}, #{type}
)

insert into comments(
	articleno, userid, content
) values (
	#{articleno}, #{userid}, #{content}
)

mapper.update:
