mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

mapper.update:
update ssafy_member 
set username = #{username}, 
userpwd = #{userpwd}, 
email = #{email}
where userid = #{userid}

mapper.select:
select username, userid, email, filename, joindate
from ssafy_member
where userid = #{userid}
